mapper.update:
UPDATE schedules
SET route_id = #{routeId}, vehicle_id = #{vehicleId}, driver_id = #{driverId},
    departure_time = #{departureTime}, status = #{status}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT id, route_id, vehicle_id, driver_id, departure_time, status, created_at, updated_at
FROM schedules
WHERE route_id = #{routeId}
